CREATE OR REPLACE FUNCTION pls_qt_vidas_grupo_contrato ( nr_seq_grupo_p bigint, ie_opcao_p text, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
QV - Quantidade de vidas
QVN - Quantidade de vidas n√£o liberadas
*/
/*
dt_referencia_p
Data utilizada como referencia para a contagem da quantidade de vidas do contrato
 */
ds_retorno_w			varchar(255);



BEGIN
if (ie_opcao_p = 'QV') then
	select	sum(qt_beneficiarios)
	into STRICT	ds_retorno_w
	from	(	SELECT	sum(1) qt_beneficiarios
			from	pls_segurado		a,
				pls_contrato		b,
				pls_contrato_grupo	d,
				pls_grupo_contrato	c
			where	a.nr_seq_contrato	= b.nr_sequencia
			and	d.nr_seq_contrato	= b.nr_sequencia
			and	d.nr_seq_grupo		= c.nr_sequencia
			and	c.nr_sequencia		= nr_seq_grupo_p
			and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
			and	((a.dt_rescisao > trunc(dt_referencia_p) and (a.dt_rescisao IS NOT NULL AND a.dt_rescisao::text <> ''))
			or (coalesce(a.dt_rescisao::text, '') = ''))
			
union all

			SELECT	sum(1) qt_beneficiarios
			from	pls_segurado		a,
				pls_intercambio		b,
				pls_contrato_grupo	d,
				pls_grupo_contrato	c
			where	a.nr_seq_intercambio	= b.nr_sequencia
			and	d.nr_seq_intercambio	= b.nr_sequencia
			and	d.nr_seq_grupo		= c.nr_sequencia
			and	c.nr_sequencia		= nr_seq_grupo_p
			and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
			and	((a.dt_rescisao > trunc(dt_referencia_p) and (a.dt_rescisao IS NOT NULL AND a.dt_rescisao::text <> ''))
			or (coalesce(a.dt_rescisao::text, '') = ''))) alias18;
elsif (ie_opcao_p = 'QVN') then
	select	sum(qt_beneficiarios)
	into STRICT	ds_retorno_w
	from	(	SELECT	sum(1) qt_beneficiarios
			from	pls_segurado		a,
				pls_contrato		b,
				pls_contrato_grupo	d,
				pls_grupo_contrato	c
			where	a.nr_seq_contrato	= b.nr_sequencia
			and	d.nr_seq_contrato	= b.nr_sequencia
			and	d.nr_seq_grupo		= c.nr_sequencia
			and	c.nr_sequencia		= nr_seq_grupo_p
			and	((a.dt_rescisao > trunc(dt_referencia_p) and (a.dt_rescisao IS NOT NULL AND a.dt_rescisao::text <> ''))
			or (coalesce(a.dt_rescisao::text, '') = ''))
			
union all

			SELECT	sum(1) qt_beneficiarios
			from	pls_segurado		a,
				pls_intercambio		b,
				pls_contrato_grupo	d,
				pls_grupo_contrato	c
			where	a.nr_seq_intercambio	= b.nr_sequencia
			and	d.nr_seq_intercambio	= b.nr_sequencia
			and	d.nr_seq_grupo		= c.nr_sequencia
			and	c.nr_sequencia		= nr_seq_grupo_p
			and	((a.dt_rescisao > trunc(dt_referencia_p) and (a.dt_rescisao IS NOT NULL AND a.dt_rescisao::text <> ''))
			or (coalesce(a.dt_rescisao::text, '') = ''))) alias16;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
