CREATE OR REPLACE FUNCTION pls_receita_ficha_relac ( nr_seq_grupo_p bigint, nr_seq_contrato_p text) RETURNS bigint AS $body$
DECLARE


vl_receita_w		double precision;


BEGIN

select	coalesce(sum(vl_mensalidade),0) vl_mensalidade
into STRICT	vl_receita_w
from	pls_contrato_grupo	d,
	pls_contrato		c,
	pls_contrato_pagador	b,
	pls_mensalidade		a
where	d.nr_seq_contrato	= c.nr_sequencia
and	b.nr_seq_contrato	= c.nr_sequencia
and	a.nr_seq_pagador	= b.nr_sequencia
and	d.nr_seq_grupo		= nr_seq_grupo_p
and	((c.nr_sequencia	= nr_seq_contrato_p)
or ('0'	= nr_seq_contrato_p))
and	coalesce(a.ie_cancelamento::text, '') = '';


return	vl_receita_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
