CREATE OR REPLACE FUNCTION pls_regra_cons_util_benef (dt_inicio_p timestamp, dt_fim_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

mensagem_w		varchar(500);
dt_inicio_apresent_w	timestamp;
dt_fim_apresent_w	timestamp;
qt_dias_limite_w	bigint;
qt_dias_w		bigint;


BEGIN

select	dt_inicio_apresent,
	dt_fim_apresent,
	qt_dias_limite
into STRICT	dt_inicio_apresent_w,
	dt_fim_apresent_w,
	qt_dias_limite_w
from 	pls_param_util_benef
where	cd_estabelecimento = cd_estabelecimento_p;

if (dt_inicio_p < coalesce(dt_inicio_apresent_w,dt_inicio_p)) then
	mensagem_w := 'Data de pesquisa fora da faixa permitida, a data de inicio da pesquisa deve ser maior que '||dt_inicio_apresent_w||'.';
elsif (dt_inicio_p > coalesce(dt_fim_apresent_w,dt_inicio_p)) then
	mensagem_w := 'Data de pesquisa fora da faixa permitida, a data de inicio da pesquisa deve ser menor que '||dt_fim_apresent_w||'.';
end if;

if (dt_fim_p < coalesce(dt_inicio_apresent_w,dt_fim_p)) then
	mensagem_w := 'Data de pesquisa fora da faixa permitida, a data final da pesquisa deve ser maior que '||dt_inicio_apresent_w||'.';
elsif (dt_fim_p > coalesce(dt_fim_apresent_w,dt_fim_p)) then
	mensagem_w := 'Data de pesquisa fora da faixa permitida, a data final da pesquisa deve ser menor que '||dt_fim_apresent_w||'.';
end if;

qt_dias_w := dt_fim_p - dt_inicio_p;

if ( (qt_dias_limite_w IS NOT NULL AND qt_dias_limite_w::text <> '') and qt_dias_w > qt_dias_limite_w) then
	mensagem_w := 'O intervalo entre a data de inicio e fim da pesquisa deve ser de no m√°ximo '||qt_dias_limite_w||' dias.';
end if;

return	mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
