SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_retorna_sql_novo_rec_glosa ( nr_seq_conta_p pls_conta.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [x] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(2);
qt_proc_w			varchar(2);
qt_mat_w			varchar(2);



BEGIN
select	count(1)
into STRICT	qt_proc_w
from	pls_conta_proc a
where	a.nr_seq_conta = nr_seq_conta_p
and	not exists (SELECT	1
		from	w_pls_recurso_glosa x
		where	x.nr_seq_conta_proc = a.nr_sequencia
		and	x.ie_tipo_inclusao = 'P')
and	a.vl_glosa > 0;

select	count(1)
into STRICT	qt_mat_w
from	pls_conta_mat a
where	a.nr_seq_conta = nr_seq_conta_p
and	not exists (SELECT	1
		from	w_pls_recurso_glosa x
		where	x.nr_seq_conta_mat = a.nr_sequencia
		and	x.ie_tipo_inclusao = 'M')
and	a.vl_glosa > 0;

if (coalesce(qt_proc_w,0) > 0) or (coalesce(qt_mat_w,0) > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
