CREATE OR REPLACE FUNCTION pls_retorno_campo_function ( nr_sequencia_1 bigint, nr_sequencia_2 bigint, nr_sequencia_3 bigint, ie_tipo_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(100);


BEGIN 
 
/* 
 
 
Por favor, de maneira nenhuma atribua novo parametro sobre esta function, pois ela eh utilizada em v√°rios lugares....... 
 
 
 */
 
 
if (ie_tipo_p = 1) then 
	 
	select	coalesce(max(nr_sequencia),0) 
	into STRICT	ds_retorno_w 
	from	pls_contrato_pagador 
	where	ie_tipo_pagador  = 'P' 
	and	nr_sequencia <> nr_sequencia_1 
	and	nr_seq_contrato = nr_sequencia_2;
 
elsif (ie_tipo_p = 2) then 
 
	select	count(*) 
	into STRICT	ds_retorno_w 
	from	pls_contrato_plano 
	where	nr_seq_contrato = nr_sequencia_1;
 
elsif (ie_tipo_p = 3) then 
 
	select	max(nr_seq_plano) 
	into STRICT	ds_retorno_w 
	from	pls_contrato_plano 
	where	nr_seq_contrato = nr_sequencia_1;
 
elsif (ie_tipo_p = 4) then 
 
	select	max(nr_seq_tabela) 
	into STRICT	ds_retorno_w 
	from	pls_contrato_plano 
	where	nr_seq_contrato = nr_sequencia_1;
	 
elsif (ie_tipo_p = 5) then 
 
	select	count(*) 
	into STRICT	ds_retorno_w 
	from	pls_contrato_pagador 
	where	nr_seq_contrato = nr_sequencia_1 
	and	ie_tipo_pagador = 'P' 
	and	coalesce(dt_rescisao::text, '') = '';
	 
elsif (ie_tipo_p = 6) then 
 
	select	max(nr_sequencia) 
	into STRICT	ds_retorno_w 
	from	pls_contrato_pagador 
	where	nr_seq_contrato = nr_sequencia_1 
	and	ie_tipo_pagador = 'P' 
	and	coalesce(dt_rescisao::text, '') = '';
	 
elsif (ie_tipo_p = 7) then 
 
	select	max(nr_seq_motivo_inclusao) 
	into STRICT	ds_retorno_w 
	from	pls_parametros 
	where	cd_estabelecimento = nr_sequencia_1;
	 
elsif (ie_tipo_p = 8) then 
 
	select	coalesce(max(nr_seq_seg_contrato),0) + 1 
	into STRICT	ds_retorno_w 
	from	pls_segurado 
	where	nr_seq_contrato = nr_sequencia_1;
	 
elsif (ie_tipo_p = 9) then 
 
	select	max(qt_idade_sca) 
	into STRICT	ds_retorno_w 
	from	pls_plano 
	where	nr_sequencia = nr_sequencia_1;
 
elsif (ie_tipo_p = 10) then 
 
	select max(ie_inadimplencia) 
	into STRICT	ds_retorno_w 
	from  pls_motivo_cancelamento 
	where  nr_sequencia = nr_sequencia_1;
	 
elsif (ie_tipo_p = 11) then 
 
	select	count(*) 
	into STRICT	ds_retorno_w 
	from	pls_mensalidade a, 
		titulo_receber b 
    	where a.nr_seq_contrato = nr_sequencia_1 
	and  substr(pls_obter_titulo_mensalidade(a.nr_sequencia,null),1,255) = b.nr_titulo 
	and  b.ie_situacao = '1';
	 
elsif (ie_tipo_p = 12) then 
 
	select	max(nr_sequencia) 
	into STRICT	ds_retorno_w 
	from	pls_empresa_vinculo 
	where	cd_estabelecimento = nr_sequencia_1;
 
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
