CREATE OR REPLACE FUNCTION pls_se_grupo_preco_contrato ( nr_seq_grupo_p bigint, nr_seq_contrato_p bigint, nr_seq_contrato_intercambio_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1)	:= 'N';
qt_grupo_contrato_w		integer;


BEGIN
--dgkorz dividi em dois count com o intuito de otimizar a performance uma vez que cada segurado podera ou ter um contragto de intercambio ou um contrato de vinculo direto com a operadora
if (coalesce(nr_seq_contrato_p, 0) > 0) then
	select  count(1)
	into STRICT	qt_grupo_contrato_w
	from   	pls_preco_contrato  a
       where  	a.nr_seq_grupo       = nr_seq_grupo_p
       and    	a.nr_seq_contrato    = nr_seq_contrato_p;
elsif (coalesce(nr_seq_contrato_intercambio_p, 0)	> 0) then
	select 	count(1)
	into STRICT	qt_grupo_contrato_w
	from  	pls_preco_contrato	a
	where	a.nr_seq_grupo		= nr_seq_grupo_p
	and	a.nr_seq_intercambio 	= nr_seq_contrato_intercambio_p;
end if;

if (qt_grupo_contrato_w > 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
