CREATE OR REPLACE FUNCTION pls_se_grupo_preco_prestador ( nr_seq_grupo_p pls_preco_prestador.nr_seq_grupo%type, nr_seq_prestador_p pls_preco_prestador.nr_seq_prestador%type, nr_seq_classificacao_p pls_preco_prestador.nr_seq_classificacao%type) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
qt_regra_w	integer;


BEGIN
-- Inicializar as variáveis
ie_retorno_w	:= 'N';
qt_regra_w	:= 0;

/* Francisco - 22/05/2012 - Select primeiro, performance */

-- Contar as regras que se encaixam nas características do prestador
select	count(1)
into STRICT	qt_regra_w
from	pls_preco_prestador a
where	a.nr_seq_grupo = nr_seq_grupo_p
and	((coalesce(a.nr_seq_prestador::text, '') = '') or (a.nr_seq_prestador = nr_seq_prestador_p))
and	((coalesce(a.nr_seq_classificacao::text, '') = '') or (a.nr_seq_classificacao = nr_seq_classificacao_p))  LIMIT 1;

-- Se alguma regra atender então o prestador está neste grupo
if (qt_regra_w > 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
