CREATE OR REPLACE FUNCTION pls_se_grupo_preco_produto ( nr_seq_grupo_p bigint, nr_seq_plano_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1)	:= 'N';
qt_preco_produto_w		integer;


BEGIN

select	count(1)
into STRICT	qt_preco_produto_w
from	pls_preco_produto	a
where	a.nr_seq_grupo		= nr_seq_grupo_p
and	a.nr_seq_plano		= nr_seq_plano_p;

if (qt_preco_produto_w > 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
