CREATE OR REPLACE FUNCTION pls_se_grupo_preco_servico_lib ( nr_seq_grupo_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


/*
------------------------------------------------------------------------------------------------------------------
ATENCAO !

NÃO UTILIZAR MAIS ESSA FUNCTION
USAR SEMPRE A PLS_SE_GRUPO_PRECO_SERVICO
-------------------------------------------------------------------------------------------------------------------
*/
ie_estrutura_w			varchar(1)	:= 'N';
cd_area_procedimento_w		bigint	:= 0;
cd_especialidade_w		bigint 	:= 0;
cd_grupo_proc_w			bigint 	:= 0;
ie_origem_proced_w		bigint;

C01 CURSOR FOR
	SELECT	/*+ CHOOSE*/		coalesce(a.ie_estrutura,'S')
	from	pls_preco_servico	a
	where	a.nr_seq_grupo	= nr_seq_grupo_p
	and	((coalesce(a.cd_procedimento::text, '') = '') or (a.cd_procedimento = cd_procedimento_p))
	and	((coalesce(a.ie_origem_proced::text, '') = '') or (a.ie_origem_proced = ie_origem_proced_w))
	and	((coalesce(a.cd_grupo_proc::text, '') = '') or (a.cd_grupo_proc = cd_grupo_proc_w))
	and	((coalesce(a.cd_especialidade::text, '') = '') or (a.cd_especialidade = cd_especialidade_w))
	and	((coalesce(a.cd_area_procedimento::text, '') = '') or (a.cd_area_procedimento = cd_area_procedimento_w))
	order by
		coalesce(a.cd_procedimento,0),
		coalesce(a.cd_grupo_proc,0),
		coalesce(a.cd_especialidade,0),
		coalesce(a.cd_area_procedimento,0);


BEGIN

/* Obter a estrutura do procedimento */

/*

Francisco - 20/10/2012 - OS 508949 - Unificar as rotinas de grupo de serviço
Chamar a PLS_SE_GRUPO_PRECO_SERVICO

pls_obter_estrut_proc(cd_procedimento_p, ie_origem_proced_p, cd_area_procedimento_w,
	cd_especialidade_w, cd_grupo_proc_w, ie_origem_proced_w);

open C01;
loop
fetch C01 into
	ie_estrutura_w;
exit when C01%notfound;
end loop;
close C01;*/
ie_estrutura_w	:= pls_se_grupo_preco_servico(nr_seq_grupo_p,cd_procedimento_p,ie_origem_proced_p);

return	ie_estrutura_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
