CREATE OR REPLACE FUNCTION pls_se_lib_protocolo ( ie_origem_protocolo_p pls_protocolo_conta.ie_origem_protocolo%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


/*
Criada com base em cursor, pois mesmo que
atualmente tenha somente um campo na restrição
futuramente os cliente podem solicitar melhorias na regra
com isso a estrutura já está pronta, sendo necessário apenas alimentar
*/
ie_controla_estab_w		pls_controle_estab.ie_controle_producao%type := 'N';

C01 CURSOR(	ie_controla_estab_pc	pls_controle_estab.ie_controle_producao%type,
		cd_estabelecimento_pc	estabelecimento.cd_estabelecimento%type,
		ie_origem_protocolo_pc	pls_protocolo_conta.ie_origem_protocolo%type) FOR
	SELECT	ie_lib_protocolo,
		ie_origem_protocolo
	from	pls_regra_lib_protocolo
	where	ie_controla_estab_pc = 'N'
	and (coalesce(ie_origem_protocolo::text, '') = '' or ie_origem_protocolo = ie_origem_protocolo_pc)
	
union all

	SELECT	ie_lib_protocolo,
		ie_origem_protocolo
	from	pls_regra_lib_protocolo
	where	ie_controla_estab_pc = 'S'
	and	cd_estabelecimento = cd_estabelecimento_pc
	and (coalesce(ie_origem_protocolo::text, '') = '' or ie_origem_protocolo = ie_origem_protocolo_pc)
	order by ie_origem_protocolo;

ie_libera_w	pls_regra_lib_protocolo.ie_lib_protocolo%type;

BEGIN

ie_controla_estab_w := pls_obter_se_controle_estab('CPM');

for r_C01_w in C01(ie_controla_estab_w, cd_estabelecimento_p, ie_origem_protocolo_p) loop
	ie_libera_w := coalesce(r_C01_w.ie_lib_protocolo,'N');
	exit;
end loop;

return	ie_libera_w;

end;
$body$
