SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_sib_obter_lote_status ( nr_seq_status_p pls_segurado_status.nr_sequencia%type, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_lote_w	pls_sib_lote.nr_sequencia%type;


BEGIN
nr_seq_lote_w	:= null;

if (ie_opcao_p = 'I') then --Inclusão
	select	max(nr_seq_lote)
	into STRICT	nr_seq_lote_w
	from	pls_sib_movimento
	where	nr_seq_status_inclusao	= nr_seq_status_p;
elsif (ie_opcao_p = 'E') then --Exclusão
	select	max(nr_seq_lote)
	into STRICT	nr_seq_lote_w
	from	pls_sib_movimento
	where	nr_seq_status_exclusao	= nr_seq_status_p;
end if;

return	nr_seq_lote_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
