CREATE OR REPLACE FUNCTION pls_sip_obter_qt_regra_duplic ( nr_seq_regra_p sip_item_assist_regra_nv.nr_sequencia%Type) RETURNS bigint AS $body$
DECLARE

qt_retorno_w	bigint;

BEGIN

qt_retorno_w := 0;

if (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') then
	select	count(a.nr_sequencia)
	into STRICT	qt_retorno_w
	from	sip_item_assist_regra_nv	a,
		sip_item_assistencial		b
	where	a.nr_sequencia <> nr_seq_regra_p
	and	a.ie_situacao = 'A'
	and	b.nr_sequencia = a.nr_seq_item_assist
	and	b.ie_permite_regra = 'S'
	and	a.ds_controle_regra_duplic = (	SELECT	max(x.ds_controle_regra_duplic)
						from	sip_item_assist_regra_nv	x,
							sip_item_assistencial		y
						where	x.nr_sequencia = nr_seq_regra_p
						and	x.ie_situacao = 'A'
						and	y.nr_sequencia = x.nr_seq_item_assist
						and	y.ie_permite_regra = 'S');
end if;


return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
