CREATE OR REPLACE FUNCTION pls_sip_obt_mens_regra_duplic ( nr_seq_regra_p sip_item_assist_regra_nv.nr_sequencia%Type) RETURNS varchar AS $body$
DECLARE


qt_regra_duplic_w	bigint;
ds_mensagem_padrao_w	varchar(1000);
ds_mensagem_w		varchar(1000);

ds_retorno_w		varchar(1000) := '';

BEGIN

ds_mensagem_padrao_w := 'Para verificar com maior facilidade as regras que estão duplicadas, pode ser usada a pasta Regras duplicadas, localizada em: '||chr(10)||
			'OPS - SIP - Sistema de Informações de Produtos \ SIP \ Consulta \ Regras duplicadas, aonde será exibido as regras por item '||chr(10)||
			'assistencial. '||chr(10)||'Na pasta Regras duplicadas, para uma melhor visualização dos registros, pode ser efetuado um duplo clique sobre '||chr(10)||
			'cada linha da consulta.';
ds_mensagem_w := '';

qt_regra_duplic_w := 0;

select	pls_sip_obter_qt_regra_duplic(nr_seq_regra_p)
into STRICT	qt_regra_duplic_w
;

if (qt_regra_duplic_w > 0) then
	ds_mensagem_w := 'Foi identificado que já existe(m) '||qt_regra_duplic_w||' regra(s) idêntica(s) a esta cadastrada(s). Favor verificar qual das regras é realmente necessária.'||chr(10)||chr(13)||chr(10)||ds_mensagem_padrao_w;
end if;

ds_retorno_w := ds_mensagem_w;

return	ds_retorno_w;

end;
$body$
