SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_status_solic_carteira ( nr_seq_carteira_p bigint, ie_retorno_p text default 'D') RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(120);
ie_sit_carteira_segurado_w		varchar(1);
ie_situacao_carteira_emissao_w		varchar(1);
ie_situacao_lote_w			varchar(1);
nr_seq_lote_w				bigint;
nr_seq_lote_emissao_w			bigint;
dt_entrega_beneficiario_w		timestamp;
dt_envio_lote_w				timestamp;
qt_solicitacao_pendente_w		bigint;
vl_dominio_w				varchar(10);


BEGIN

select	count(*)
into STRICT	qt_solicitacao_pendente_w
from	pls_solic_carteira_adic
where	nr_seq_carteira	= nr_seq_carteira_p
and	ie_status	= 0; --Pendente
if (qt_solicitacao_pendente_w > 0) then
	if (ie_retorno_p = 'C') then
		ds_retorno_w	:= 1;
	elsif (ie_retorno_p = 'D') then
		ds_retorno_w	:= obter_valor_dominio(2587, 1); --1 - Analise
	end if;	
else
	-- Obter a situacao da carteira do segurado
	select	coalesce(max(ie_situacao), 'D'),
		max(nr_seq_lote_emissao)
	into STRICT	ie_sit_carteira_segurado_w,
		nr_seq_lote_emissao_w
	from	pls_segurado_carteira
	where	nr_sequencia	= nr_seq_carteira_p;

	-- Obter o ultimo lote da carteirinha
	select	coalesce(max(b.nr_seq_lote),0)
	into STRICT	nr_seq_lote_w
	from	pls_carteira_emissao	b,
		pls_segurado_carteira	a
	where	a.nr_sequencia		= b.nr_seq_seg_carteira
	and	a.nr_sequencia		= nr_seq_carteira_p;

	-- Obter o status e o recebimento da carteira pela grafica
	select	coalesce(max(ie_situacao), 'P'),
		max(dt_entrega_beneficiario)
	into STRICT	ie_situacao_carteira_emissao_w,
		dt_entrega_beneficiario_w
	from	pls_carteira_emissao
	where	nr_seq_seg_carteira	= nr_seq_carteira_p
	and	nr_seq_lote		= nr_seq_lote_w;

	select	max(dt_envio),
		coalesce(max(ie_situacao), 'D')
	into STRICT	dt_envio_lote_w,
		ie_situacao_lote_w
	from	pls_lote_carteira
	where	nr_sequencia	= nr_seq_lote_w;

	if (ie_sit_carteira_segurado_w = 'P') and (coalesce(nr_seq_lote_emissao_w::text, '') = '') then
		vl_dominio_w	:= '1'; -- Analise
	elsif (dt_entrega_beneficiario_w IS NOT NULL AND dt_entrega_beneficiario_w::text <> '') then
		vl_dominio_w	:= '4'; -- Recebida
	elsif (ie_situacao_carteira_emissao_w = 'D') and (coalesce(dt_entrega_beneficiario_w::text, '') = '') then
		vl_dominio_w	:= '3'; -- Disponivel para entrega
	elsif (ie_situacao_carteira_emissao_w = 'P') then
		vl_dominio_w	:= '2'; -- Lote gerado
	elsif (ie_sit_carteira_segurado_w = 'P') then
		vl_dominio_w	:= '1'; -- Analise
	end if;
	
	if (ie_retorno_p = 'C') then
		ds_retorno_w	:= vl_dominio_w;
	elsif (ie_retorno_p = 'D') then
		ds_retorno_w	:= obter_valor_dominio(2587, vl_dominio_w);
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
