CREATE OR REPLACE FUNCTION pls_substituir_macro_carteira ( nr_sequencia_p bigint, nm_atributo_p text, ds_macro_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_plano_w		bigint;
nr_seq_contrato_w	bigint;
nr_seq_pagador_w	bigint;
nr_seq_pagador_fin_w	bigint;
ds_banco_w		varchar(255);


BEGIN

select	nr_seq_plano,
	nr_seq_contrato,
	nr_seq_pagador
into STRICT	nr_seq_plano_w,
	nr_seq_contrato_w,
	nr_seq_pagador_w
from	pls_segurado
where	nr_sequencia	= nr_sequencia_p;

if (upper(nm_atributo_p)	= 'NR_SEQ_CONTRATO') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_pagador_fin_w
	from	pls_contrato_pagador_fin
	where	nr_seq_pagador		= nr_seq_pagador_w
	and	trunc(clock_timestamp()) between dt_inicio_vigencia and fim_dia(dt_fim_vigencia)
	and	cd_tipo_portador	= 1;

	if (nr_seq_pagador_fin_w IS NOT NULL AND nr_seq_pagador_fin_w::text <> '') then
		select	substr(obter_desc_portador(cd_tipo_portador, cd_portador),1,255)
		into STRICT	ds_banco_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia	= nr_seq_pagador_fin_w;
	end if;

	if (upper(ds_macro_p)	= '@ESTIPULANTE_CONTRATO') then
		ds_retorno_w		:= substr(pls_obter_dados_contrato(nr_seq_contrato_w,'E'),1,255);
	elsif (upper(ds_macro_p)	= '@BANCO_PAG') then
		ds_retorno_w		:= ds_banco_w;
	end if;
elsif (upper(nm_atributo_p)	= 'NR_SEQ_PLANO') then
	if (upper(ds_macro_p)	= '@AREA_ACAO_PRODUTO') then
		ds_retorno_w		:= substr(pls_obter_dados_cart_unimed(nr_sequencia_p,nr_seq_plano_w,'DR',1),1,255);

	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
