SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_substituir_macro_evento ( nr_seq_pessoa_evento_p bigint, ds_macro_p text, valor_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w			varchar(2000);
ds_cabecalho_w			varchar(2000);


BEGIN

if (coalesce(nr_seq_pessoa_evento_p,0) > 0) then
	select	substr(max(ds_cabecalho),1,2000)
	into STRICT	ds_cabecalho_w
	from	pls_evento_pessoa_portal
	where	nr_sequencia	= nr_seq_pessoa_evento_p;

	if (ds_macro_p = '@prestador_executor') then
		ds_resultado_w := substr(replace_macro(ds_cabecalho_w,'@prestador_executor',valor_p),1,2000);
	end if;
end if;

return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
