CREATE OR REPLACE FUNCTION pls_tabela_simul_unica_regra ( nr_seq_plano_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_tabela_w		bigint;
qt_registros_w			bigint;


BEGIN

select	count(1)
into STRICT	qt_registros_w
from	pls_regra_simulador_web
where	nr_seq_plano	= nr_seq_plano_p
and	clock_timestamp() between dt_inicio_vigencia and coalesce(dt_fim_vigencia,clock_timestamp());

if (qt_registros_w = 1) then
	select	max(nr_seq_tabela)
	into STRICT	nr_seq_tabela_w
	from	pls_regra_simulador_web
	where	nr_seq_plano	= nr_seq_plano_p
	and	clock_timestamp() between dt_inicio_vigencia and coalesce(dt_fim_vigencia,clock_timestamp());
end if;

return	nr_seq_tabela_w;

end;
$body$
