SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_tiss_calcular_cred_deb (nr_seq_protocolo_p bigint, nm_usuario_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
vl_credito_w		double precision;
vl_debito_w		double precision;
vl_liberacao_w		double precision;
vl_credito_ww		double precision;
vl_debito_ww		double precision;
vl_liberacao_ww		double precision;

/* ie_opcao_p
	D - Demonstrativo
	P - Data de Pagamento
*/
BEGIN
if (ie_opcao_p 	= 'D') then
	select	coalesce(sum(vl_liberado),0)
	into STRICT	vl_liberacao_w
	from	w_pls_resumo_protocolo
	where	nm_usuario	= nm_usuario_p;

	select 	coalesce(sum(vl_debito_cred),0)
	into STRICT	vl_debito_w
	from	w_pls_mov_pagamento
	where	ie_indicacao = 'D'
	and 	nr_seq_protocolo_w = nr_seq_protocolo_p;

	select 	coalesce(sum(vl_debito_cred),0)
	into STRICT	vl_credito_w
	from	w_pls_mov_pagamento
	where	ie_indicacao = 'P'
	and 	nr_seq_protocolo_w = nr_seq_protocolo_p;

	vl_retorno_w	:= vl_liberacao_w - vl_debito_w + vl_credito_w;

elsif (ie_opcao_p	= 'P') then
	select	coalesce(sum(vl_liberado),0)
	into STRICT	vl_liberacao_ww
	from	w_pls_resumo_protocolo
	where	nr_seq_protocolo_w	= nr_seq_protocolo_p;

	select 	coalesce(sum(vl_debito_cred),0)
	into STRICT	vl_debito_ww
	from	w_pls_mov_pagamento
	where	ie_indicacao = 'D'
	and 	nr_seq_protocolo_w = nr_seq_protocolo_p;

	select 	coalesce(sum(vl_debito_cred),0)
	into STRICT	vl_credito_ww
	from	w_pls_mov_pagamento
	where	ie_indicacao = 'P'
	and 	nr_seq_protocolo_w = nr_seq_protocolo_p;

	vl_retorno_w	:= vl_liberacao_ww - vl_debito_ww + vl_credito_ww;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
