CREATE OR REPLACE FUNCTION pls_total_titulo_lote ( nr_seq_lote_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision;


BEGIN
begin
select	coalesce(sum(vl_total_fatura),0)
into STRICT	vl_retorno_w
from	ptu_fatura
where	nr_seq_lote	= nr_seq_lote_p;
exception
when others then
	vl_retorno_w	:= 0;
end;

return	vl_retorno_w;

end;
$body$
