CREATE OR REPLACE FUNCTION pls_tps_obter_desconto_reg ( ie_abrangencia_p text, ie_segmentacao_p text) RETURNS bigint AS $body$
DECLARE


pr_desc_abrangencia_w		double precision	:= 0;
pr_desc_segmentacao_w		double precision	:= 0;
pr_desc_retorno_w		double precision	:= 0;


BEGIN

select	coalesce(max(pr_desconto),0)
into STRICT	pr_desc_abrangencia_w
from	pls_tps_desc_abrangencia
where	ie_abrangencia	= ie_abrangencia_p;

select	coalesce(max(pr_desconto),0)
into STRICT	pr_desc_segmentacao_w
from	pls_tps_desc_cobertura
where	ie_segmentacao	= ie_segmentacao_p;

pr_desc_retorno_w	:= pr_desc_abrangencia_w + pr_desc_segmentacao_w;

return	pr_desc_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
