CREATE OR REPLACE FUNCTION pls_tps_obter_meses_periodo ( nr_seq_tps_p bigint, ie_mes_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(20);
dt_primeiro_mes_w		timestamp;
dt_segundo_mes_w		timestamp;
dt_terceiro_mes_w		timestamp;


BEGIN

select	add_months(dt_periodo_inicial, ie_mes_p),
	add_months(dt_periodo_inicial, ie_mes_p),
	add_months(dt_periodo_inicial, ie_mes_p)
into STRICT	dt_primeiro_mes_w,
	dt_segundo_mes_w,
	dt_terceiro_mes_w
from	pls_tps
where	nr_sequencia	= nr_seq_tps_p;

if (ie_mes_p	= 0) then
	ds_retorno_w	:= to_char(dt_primeiro_mes_w, 'mm/yyyy');
elsif (ie_mes_p	= 1) then
	ds_retorno_w	:= to_char(dt_segundo_mes_w, 'mm/yyyy');
elsif (ie_mes_p	= 2) then
	ds_retorno_w	:= to_char(dt_terceiro_mes_w, 'mm/yyyy');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
