CREATE OR REPLACE FUNCTION pls_utimo_registros_reajuste ( nr_seq_reajuste_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE

/* ie_opcao_p	1 = peneltima registro
		2 = utimo registro*/
nr_sequencia_w			bigint;
pr_reajustado_w			double precision;


BEGIN
if (ie_opcao_p	= 1) then
select	nr_sequencia,
	pr_reajustado
into STRICT	nr_sequencia_w,
	pr_reajustado_w
from	pls_reajuste_preco
where	nr_seq_preco	= nr_seq_reajuste_p
and	nr_sequencia	= (	SELECT	max(nr_sequencia)
				from	pls_reajuste_preco
				where	nr_seq_preco	= nr_seq_reajuste_p
				and	nr_Sequencia	<> (	select	max(nr_sequencia)
								from	pls_reajuste_preco
								where	nr_seq_preco = nr_seq_reajuste_p));
end if;
if (ie_opcao_p	= 2) then
select	nr_sequencia,
	pr_reajustado
into STRICT	nr_sequencia_w,
	pr_reajustado_w
from	pls_reajuste_preco
where	nr_seq_preco	= nr_seq_reajuste_p
and	nr_sequencia	= (	SELECT	max(nr_sequencia)
				from	pls_reajuste_preco
				where	nr_seq_preco	= nr_seq_reajuste_p);
end if;
return	pr_reajustado_w;

end;
$body$
LANGUAGE PLPGSQL
