CREATE OR REPLACE FUNCTION pls_valida_caract_decl ( nr_seq_conta_p pls_conta.nr_sequencia%type, ds_carac_valido_p pls_oc_cta_val_inf_nasc.ds_carac_valido%type, ie_evento_p pls_oc_cta_combinada.ie_evento%type) RETURNS varchar AS $body$
DECLARE


ie_gera_glosa_w			varchar(1) := 'S';
nr_decl_nasc_vivo_w		pls_diagnostico_nasc_vivo.nr_decl_nasc_vivo%type;
nr_decl_nasc_vivo_alt_w		pls_diagnostico_nasc_vivo.nr_decl_nasc_vivo%type;


BEGIN

if (ie_evento_p = 'IMP') then
	select	max(nr_decl_nasc_vivo_imp)
	into STRICT	nr_decl_nasc_vivo_w
	from	pls_diagnostico_nasc_vivo
	where	nr_seq_conta = nr_seq_conta_p;
else
	select	max(nr_decl_nasc_vivo)
	into STRICT	nr_decl_nasc_vivo_w
	from	pls_diagnostico_nasc_vivo
	where	nr_seq_conta = nr_seq_conta_p;
end if;

if (nr_decl_nasc_vivo_w IS NOT NULL AND nr_decl_nasc_vivo_w::text <> '') then
	nr_decl_nasc_vivo_alt_w := regexp_replace(nr_decl_nasc_vivo_w, '[^' || ds_carac_valido_p || ']', '');

	if	((nr_decl_nasc_vivo_alt_w <> nr_decl_nasc_vivo_w) or (coalesce(nr_decl_nasc_vivo_alt_w::text, '') = '')) then
		ie_gera_glosa_w := 'N';
	end if;
end if;

return	ie_gera_glosa_w;

end;
$body$
