CREATE OR REPLACE FUNCTION pls_valida_data_atual_web ( dt_entrada_p text) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Validar a data de emissão da conta
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */ds_retorno_w		varchar(1) := 'N';


BEGIN

if (dt_entrada_p IS NOT NULL AND dt_entrada_p::text <> '') then
	if (trunc(to_date(dt_entrada_p,'dd/mm/yyyy')) <= trunc(clock_timestamp())) then
		ds_retorno_w := 'S';
	end if;
end if;

return ds_retorno_w;

end;
$body$
