CREATE OR REPLACE FUNCTION pls_valida_prest_hab_outro_loc ( nr_seq_prestador_p bigint, nr_seq_prestador_web_p bigint, ie_funcao_liberada_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

				  
ds_retorno_w		varchar(255)	:='N';	
qt_registros_w		bigint;
nr_seq_regra_w		bigint;				
ds_regra_lib_w		varchar(2);


BEGIN 
 
SELECT * FROM SELECT * FROM pls_verifica_regra_lib_web(null, nr_seq_prestador_web_p, 'B', null) INTO STRICT ,  INTO 'B', null;
 
if (ds_regra_lib_w <> 'S') then 
 
	-- Verifica se o prestador esta com o FINGER desabilitado 
	if (pls_verif_est_finger_web(nr_seq_prestador_p, nr_seq_prestador_web_p) = 'NAO') then 
 
		--Verifica se o prestador esta com o FINGER habilitado com outro usuário que não seja o mesmo logado no portal 
		select	count(1) 
		into STRICT	qt_registros_w 
		from	pls_controle_biomet_prest 
		where	nr_seq_prestador = nr_seq_prestador_p 
		and	coalesce(dt_desativacao::text, '') = '' 
		and 	nr_seq_usuario_prest <> nr_seq_prestador_web_p;
 
		if (qt_registros_w > 0 )then 
			ds_retorno_w := 'S';
		end if;
	end if;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
