CREATE OR REPLACE FUNCTION pls_valida_qtd_conta_web ( qt_max_contas_p bigint, ie_tipo_guia_p text, nr_seq_prestador_p bigint, nr_seq_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE

/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Verificar o limite de quantidade de protocolos por conta
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

	ie_qtd_max_w = 	'N' : Protocolo atingiu seu limite de contas
			'S' : Protocolo não atingiu seu limite de contas
*/ie_qtd_max_w		varchar(1) := 'S';
qt_contas_w		bigint := 0;
qt_protocolo_w		bigint := 0;
nr_seq_protocolo_w      bigint;


BEGIN

nr_seq_protocolo_w := nr_seq_protocolo_p;

select	count(*)
into STRICT	qt_protocolo_w
from 	pls_protocolo_conta
where	nr_sequencia = nr_seq_protocolo_w
and	ie_tipo_guia = ie_tipo_guia_p;

if (qt_protocolo_w = 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_protocolo_w
	from 	pls_protocolo_conta
	where	ie_tipo_guia = ie_tipo_guia_p
	and 	ie_situacao = 'I'
	and	ie_status = '1'
	and	ie_apresentacao = 'A'
	and	ie_tipo_protocolo = 'C'
	and 	ie_forma_imp = 'P'
	and	nr_seq_prestador = nr_seq_prestador_p;
end if;

if (nr_seq_protocolo_w IS NOT NULL AND nr_seq_protocolo_w::text <> '') then
	select	count(*)
	into STRICT	qt_contas_w
	from 	pls_conta
	where	nr_seq_protocolo = nr_seq_protocolo_p;

	if (qt_contas_w >= qt_max_contas_p) then
		ie_qtd_max_w := 'N';
	end if;
end if;

return	ie_qtd_max_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
