CREATE OR REPLACE FUNCTION pls_valida_valor_total ( vl_unitario_p bigint, vl_total_p bigint, qt_executado_p bigint, vl_tolerancia_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se o valor total está de acordo com a quantidade executada * o valor unitário
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(1);


BEGIN
if	(vl_total_p <= (vl_unitario_p * qt_executado_p) + vl_tolerancia_p) and
		(vl_total_p >= (vl_unitario_p * qt_executado_p) - vl_tolerancia_p) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
