SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_valores_ficha_relac ( nr_seq_grupo_p bigint, nr_seq_contrato_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*ie_opcao_p
C - Consultas
E - Exames
I - Internações
*/
pr_procedimento_w	double precision;
qt_procedimento_tot_w	bigint;


BEGIN

select	count(qt_procedimento) qt_proc
into STRICT	qt_procedimento_tot_w
from	pls_tipo_atendimento	f,
	pls_contrato_grupo	e,
	pls_contrato		d,
	pls_segurado		c,
	pls_conta		b,
	pls_conta_proc		a
where	e.nr_seq_contrato	= d.nr_sequencia
and	c.nr_seq_contrato	= d.nr_sequencia
and	b.nr_seq_segurado	= c.nr_sequencia
and	a.nr_seq_conta		= b.nr_sequencia
and	b.nr_seq_tipo_atendimento	= f.nr_sequencia
and	e.nr_seq_grupo		= nr_seq_grupo_p
and	b.ie_status		= 'F'
and	((d.nr_sequencia	= nr_seq_contrato_p)
or ('0'	= nr_seq_contrato_p))
and	f.cd_tipo_atendimento	in (4,5,7);

select	sum(pr_procedimento)
into STRICT	pr_procedimento_w
from	(	SELECT	dividir(count(qt_procedimento) * 100,qt_procedimento_tot_w) pr_procedimento
		from	pls_tipo_atendimento	f,
			pls_contrato_grupo	e,
			pls_contrato		d,
			pls_segurado		c,
			pls_conta		b,
			pls_conta_proc		a
		where	e.nr_seq_contrato	= d.nr_sequencia
		and	c.nr_seq_contrato	= d.nr_sequencia
		and	b.nr_seq_segurado	= c.nr_sequencia
		and	a.nr_seq_conta		= b.nr_sequencia
		and	b.nr_seq_tipo_atendimento	= f.nr_sequencia
		and	e.nr_seq_grupo		= nr_seq_grupo_p
		and	((d.nr_sequencia	= nr_seq_contrato_p)
		or ('0'			= nr_seq_contrato_p))
		and	b.ie_status		= 'F'
		and	f.cd_tipo_atendimento	= 4
		and	ie_opcao_p		= 'C'
		
union

		SELECT	dividir(count(qt_procedimento) * 100,qt_procedimento_tot_w) pr_procedimento
		from	pls_tipo_atendimento	f,
			pls_contrato_grupo	e,
			pls_contrato		d,
			pls_segurado		c,
			pls_conta		b,
			pls_conta_proc		a
		where	e.nr_seq_contrato	= d.nr_sequencia
		and	c.nr_seq_contrato	= d.nr_sequencia
		and	b.nr_seq_segurado	= c.nr_sequencia
		and	a.nr_seq_conta		= b.nr_sequencia
		and	b.nr_seq_tipo_atendimento	= f.nr_sequencia
		and	e.nr_seq_grupo		= nr_seq_grupo_p
		and	((d.nr_sequencia	= nr_seq_contrato_p)
		or ('0'			= nr_seq_contrato_p))
		and	b.ie_status		= 'F'
		and	f.cd_tipo_atendimento	= 5
		and	ie_opcao_p		= 'E'
		
union

		select	dividir(count(qt_procedimento) * 100,qt_procedimento_tot_w) pr_procedimento
		from	pls_tipo_atendimento	f,
			pls_contrato_grupo	e,
			pls_contrato		d,
			pls_segurado		c,
			pls_conta		b,
			pls_conta_proc		a
		where	e.nr_seq_contrato	= d.nr_sequencia
		and	c.nr_seq_contrato	= d.nr_sequencia
		and	b.nr_seq_segurado	= c.nr_sequencia
		and	a.nr_seq_conta		= b.nr_sequencia
		and	b.nr_seq_tipo_atendimento	= f.nr_sequencia
		and	e.nr_seq_grupo		= nr_seq_grupo_p
		and	((d.nr_sequencia	= nr_seq_contrato_p)
		or ('0'			= nr_seq_contrato_p))
		and	b.ie_status		= 'F'
		and	f.cd_tipo_atendimento	= 7
		and	ie_opcao_p		= 'I'
		group by f.ds_tipo_atendimento	) alias16;

return	pr_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
