CREATE OR REPLACE FUNCTION pls_verificar_anexo_prop_benef (nr_seq_proposta_benef_p pls_proposta_beneficiario.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_existe_anexo_w	varchar(255);
qt_anexo_w		bigint;		
		

BEGIN

if (nr_seq_proposta_benef_p IS NOT NULL AND nr_seq_proposta_benef_p::text <> '') then
	select	count(1)
	into STRICT	qt_anexo_w
	from	pls_proposta_benef_anexo
	where	nr_seq_beneficiario = nr_seq_proposta_benef_p;
	
	if (qt_anexo_w > 0) then
		ie_existe_anexo_w	:= obter_desc_expressao(327113);
	else
		ie_existe_anexo_w	:= obter_desc_expressao(327114);
	end if;
else
	ie_existe_anexo_w	:= obter_desc_expressao(327114);
end if;

return	ie_existe_anexo_w;

end;
$body$
