CREATE OR REPLACE FUNCTION pls_verificar_anexo_rescisao ( nr_seq_solic_resc_p pls_solicitacao_rescisao.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
qt_anexo_w	bigint;	
	

BEGIN

select	count(1)
into STRICT	qt_anexo_w
from	pls_solicitacao_resc_anexo
where	nr_seq_solicitacao = nr_seq_solic_resc_p;

if (qt_anexo_w > 0) then
	ds_retorno_w := wheb_mensagem_pck.get_texto(1088712); --Sim
else
	ds_retorno_w := wheb_mensagem_pck.get_texto(1088713); --NÃ£o
end if;

return	ds_retorno_w;

end;
$body$
