CREATE OR REPLACE FUNCTION pls_verificar_cep_valido ( cd_cep_p text) RETURNS varchar AS $body$
DECLARE


ie_cep_existente_base_w		varchar(1);
qt_cep_log_w			bigint;
qt_cep_loc_w			bigint;
cd_cep_logradouro_w		bigint;


BEGIN

ie_cep_existente_base_w	:= 'N';

select	count(1)
into STRICT	qt_cep_log_w
from	cep_log
where	cd_cep	= cd_cep_p;

if (qt_cep_log_w > 0) then
	ie_cep_existente_base_w	:= 'S';
else
	select	count(1)
	into STRICT	cd_cep_logradouro_w
	from	cep_logradouro a
	where	cd_logradouro	= cd_cep_p;

	if (cd_cep_logradouro_w > 0) then
		ie_cep_existente_base_w	:= 'S';
	else
		select	count(1)
		into STRICT	qt_cep_loc_w
		from	cep_loc
		where	cd_cep	= cd_cep_p;

		if (qt_cep_loc_w > 0) then
			ie_cep_existente_base_w	:= 'S';
		end if;
	end if;
end if;

return	ie_cep_existente_base_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
