CREATE OR REPLACE FUNCTION pls_verificar_dt_atendimento ( dt_atendimento_p text, nr_seq_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	 	varchar(1)	:= 'S'; -- por padrao estÃ¡ ok
dt_protocolo_w		timestamp;
dt_atendimento_w	timestamp;


BEGIN

select	to_date(dt_atendimento_p,'dd/mm/yyyy hh:mi:ss')
into STRICT 	dt_atendimento_w
;

if (coalesce(nr_seq_protocolo_p,0) > 0) then
	begin
	select 	to_date(to_char(dt_protocolo,'dd/mm/yyyy hh:mi:ss'),'dd/mm/yyyy hh:mi:ss')
	into STRICT	dt_protocolo_w
	from 	pls_protocolo_conta
	where 	nr_sequencia = nr_seq_protocolo_p;
	exception
	when others then
		dt_protocolo_w	:= null;
		ie_retorno_w	:= 'S';
	end;

	if (dt_protocolo_w IS NOT NULL AND dt_protocolo_w::text <> '') and (dt_atendimento_w IS NOT NULL AND dt_atendimento_w::text <> '') and (dt_atendimento_w > dt_protocolo_w) then
		ie_retorno_w	:= 'N';
	end if;
end if;

return	ie_retorno_w;

end;
