SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_verificar_item_compl_web ( NR_SEQ_ITEM_P bigint, IE_TIPO_ITEM_P text, QT_ALTERADA_P bigint) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Verificar se o item é procedimento ou material
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

	ie_tipo_item_p = M: Material, P : Procedimento

 */qt_autorizado_w		bigint;
ds_retorno_w 		varchar(1) := 'N';


BEGIN

if (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then
	if (ie_tipo_item_p = 'P') then

		select	max(qt_autorizado)
		into STRICT	qt_autorizado_w
		from	pls_conta_proc
		where	nr_sequencia = nr_seq_item_p;

		if (coalesce(qt_autorizado_w::text, '') = '') then
			ds_retorno_w := 'S';
		elsif (qt_alterada_p <= qt_autorizado_w or qt_autorizado_w = 0) then
			ds_retorno_w := 'S';
		end if;

	elsif (ie_tipo_item_p = 'M') then

		select	max(qt_autorizado)
		into STRICT	qt_autorizado_w
		from	pls_conta_mat
		where	nr_sequencia = nr_seq_item_p;

		if (coalesce(qt_autorizado_w::text, '') = '') then
			ds_retorno_w := 'S';
		elsif (qt_alterada_p <= qt_autorizado_w or qt_autorizado_w = 0) then
			ds_retorno_w := 'S';
		end if;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
