CREATE OR REPLACE FUNCTION pls_verificar_regra_acesso_eve (nr_seq_evento_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_resultado_w		varchar(1) := 'N';
qt_registro_w		bigint := 0;


BEGIN
select	count(1)
into STRICT	qt_registro_w
from	pls_evento_acesso
where	nr_seq_evento 				= nr_seq_evento_p
and	coalesce(nm_usuario_param,nm_usuario_p) 	= nm_usuario_p
and	coalesce(cd_perfil,cd_perfil_p)		= cd_perfil_p;

if (qt_registro_w > 0) then
	ie_resultado_w := 'S';
else
	select	count(1)
	into STRICT	qt_registro_w
	from	pls_evento_acesso
	where	nr_seq_evento	= nr_seq_evento_p;

	if (qt_registro_w = 0) then
		ie_resultado_w := 'S';
	end if;
end if;

return	ie_resultado_w;

end;
$body$
