SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_verificar_se_canc_guia_web ( nr_seq_guia_p bigint, nr_seq_prestador_web_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
ie_estagio_w		smallint;
qt_registros_w		bigint;
cd_guia_w		varchar(20);
nr_seq_segurado_w	bigint;


BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then


	begin
		select	count(1)
		into STRICT	qt_registros_w
		from	pls_guia_plano
		where	nr_sequencia = nr_seq_guia_p
		and	nr_seq_prestador_web = nr_seq_prestador_web_p
		and	ie_status  =  '1'
		and	ie_estagio <> 8;
	exception
	when others then
		qt_registros_w := 0;
	end;


	if (qt_registros_w > 0) then

		select	cd_guia,
			nr_seq_segurado
		into STRICT	cd_guia_w,
			nr_seq_segurado_w
		from	pls_guia_plano
		where	nr_sequencia = nr_seq_guia_p;

		select	count(1)
		into STRICT	qt_registros_w
		from	pls_conta
		where	cd_guia		= cd_guia_w
		and	nr_seq_segurado	= nr_seq_segurado_w;

		if (qt_registros_w = 0) then
			ds_retorno_w := 'S';
		end if;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
