CREATE OR REPLACE FUNCTION pls_verificar_utilizacao_sca ( nr_seq_cober_carencia_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

/*
1 - Carência SCA
2 - Cobertura  SCA
3 -  Carência do contrato ou produto
4- Cobertura do contrato ou carencia
5 - Carência intercâmbio
6 - C obertura intercâmbio
*/
ds_retorno_w			varchar(2)	:= null;
nr_seq_tipo_cobertura_sca_w	bigint;
nr_seq_tipo_carencia_sca_w	bigint;
nr_seq_tipo_cobertura_w		bigint;
nr_seq_tipo_carencia_w		bigint;


BEGIN

if (ie_opcao_p = 1) then
	select	nr_sequencia
	into STRICT	nr_seq_tipo_carencia_sca_w
	from	pls_tipo_carencia
	where	nr_sequencia	= nr_seq_cober_carencia_p
	and (ie_utilizacao	in ('S','A')
	or (coalesce(ie_utilizacao::text, '') = ''))
	and	ie_situacao	= 'A';
elsif (ie_opcao_p = 2) then
	select	nr_sequencia
	into STRICT	nr_seq_tipo_cobertura_sca_w
	from	pls_tipo_cobertura
	where	nr_sequencia	= nr_seq_cober_carencia_p
	and (ie_utilizacao	in ('S','A')
	or (coalesce(ie_utilizacao::text, '') = ''))
	and	ie_situacao	= 'A';
elsif (ie_opcao_p = 3) then
	select	nr_sequencia
	into STRICT	nr_seq_tipo_carencia_w
	from	pls_tipo_carencia
	where	nr_sequencia	= nr_seq_cober_carencia_p
	and (ie_utilizacao	in ('C','A')
	or (coalesce(ie_utilizacao::text, '') = ''))
	and	ie_situacao	= 'A';
elsif (ie_opcao_p = 4) then
	select	nr_sequencia
	into STRICT	nr_seq_tipo_cobertura_w
	from	pls_tipo_cobertura
	where	nr_sequencia	= nr_seq_cober_carencia_p
	and (ie_utilizacao	in ('C','A')
	or (coalesce(ie_utilizacao::text, '') = ''))
	and	ie_situacao	= 'A';
elsif (ie_opcao_p = 5) then
	select	nr_sequencia
	into STRICT	nr_seq_tipo_carencia_w
	from	pls_tipo_carencia
	where	nr_sequencia	= nr_seq_cober_carencia_p
	and (ie_utilizacao	in ('P','A')
	or (coalesce(ie_utilizacao::text, '') = ''))
	and	ie_situacao	= 'A';
elsif (ie_opcao_p = 6) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_tipo_cobertura_w
	from	pls_tipo_cobertura
	where	nr_sequencia	= nr_seq_cober_carencia_p
	and (ie_utilizacao	in ('P','A')
	or (coalesce(ie_utilizacao::text, '') = ''))
	and	ie_situacao	= 'A';
end if;

if (ie_opcao_p = 1) then
	if (coalesce(nr_seq_tipo_carencia_sca_w,0) > 0) then
		ds_retorno_w	:= 'S';
	elsif (coalesce(nr_seq_tipo_carencia_sca_w::text, '') = '') then
		ds_retorno_w	:= 'N';
	end if;
elsif (ie_opcao_p = 2) then
	if (coalesce(nr_seq_tipo_cobertura_sca_w,0) > 0) then
		ds_retorno_w	:= 'S';
	elsif (coalesce(nr_seq_tipo_cobertura_sca_w::text, '') = '') then
		ds_retorno_w	:= 'N';
	end if;
elsif (ie_opcao_p = 3) then
	if (coalesce(nr_seq_tipo_carencia_w,0) > 0) then
		ds_retorno_w	:= 'S';
	elsif (coalesce(nr_seq_tipo_carencia_w::text, '') = '') then
		ds_retorno_w	:= 'N';
	end if;
elsif (ie_opcao_p = 4) then
	if (coalesce(nr_seq_tipo_cobertura_w,0) > 0) then
		ds_retorno_w	:= 'S';
	elsif (coalesce(nr_seq_tipo_cobertura_w::text, '') = '') then
		ds_retorno_w	:= 'N';
	end if;
elsif (ie_opcao_p = 5) then
	if (coalesce(nr_seq_tipo_carencia_w,0) > 0) then
		ds_retorno_w	:= 'S';
	elsif (coalesce(nr_seq_tipo_carencia_w::text, '') = '') then
		ds_retorno_w	:= 'N';
	end if;
elsif (ie_opcao_p = 6) then
	if (coalesce(nr_seq_tipo_cobertura_w,0) > 0) then
		ds_retorno_w	:= 'S';
	elsif (coalesce(nr_seq_tipo_cobertura_w::text, '') = '') then
		ds_retorno_w	:= 'N';
	end if;
end if;

return	ds_retorno_w;

end;
