SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_verifica_benef_eventual ( cd_usuario_plano_p text, ie_tipo_verificacao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

/* 
ie_tipo_verificacao_p 
B = Verifica se o beneficiário eventual que esta sendo criado é da própria operadora 
D = Verifica se o digito verificador da carteirinha do beneficiário é válido 
*/
 
			 
cd_cooperativa_w		varchar(30);

ie_retorno_w			varchar(2)	:= 'N';
ie_digito_valido_w		varchar(2);
ie_benef_operadora_atual_w	varchar(2);

nr_seq_emissor_w		bigint;
nr_seq_carteira_seg_w		bigint;

qt_coop_estab_w			smallint;


BEGIN 
if (ie_tipo_verificacao_p	= 'B') then 
	select	max(nr_seq_emissor) 
	into STRICT	nr_seq_emissor_w 
	from	pls_parametros 
	where	cd_estabelecimento	= cd_estabelecimento_p;
 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_carteira_seg_w 
	from	pls_segurado_carteira 
	where	cd_usuario_plano	= cd_usuario_plano_p 
	and	cd_estabelecimento	= cd_estabelecimento_p;
 
	if (coalesce(nr_seq_carteira_seg_w,0) = 0) then	/* Carteira do beneficiário não existe na base */
 
		select	pls_obter_campos_carteira(cd_usuario_plano_p,nr_seq_emissor_w,'CM') 
		into STRICT	cd_cooperativa_w 
		;
		 
		if (coalesce(cd_cooperativa_w,'0') <> '0') then 
			select	count(*) 
			into STRICT	qt_coop_estab_w 
			from	pls_congenere a, 
				pls_outorgante b 
			where	b.cd_cgc_outorgante	= a.cd_cgc 
			and	a.cd_cooperativa	= cd_cooperativa_w 
			and	a.cd_estabelecimento	= cd_estabelecimento_p;
			 
			if (qt_coop_estab_w	> 0) then 
				ie_benef_operadora_atual_w	:= 'S';
			elsif (qt_coop_estab_w 	= 0) then 
				ie_benef_operadora_atual_w	:= 'N';
			end if;
			ie_retorno_w	:= ie_benef_operadora_atual_w;
		end if;
	end if;
elsif (ie_tipo_verificacao_p	= 'D') then 
	if (pls_calcula_digito_carteira(substr(cd_usuario_plano_p, 1, length(cd_usuario_plano_p) -1)) <>  
		substr(cd_usuario_plano_p, length(cd_usuario_plano_p), 1)) then 
		ie_digito_valido_w	:= 'N';
	else 
		ie_digito_valido_w	:= 'S';
	end if;
	ie_retorno_w	:= ie_digito_valido_w;
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
