CREATE OR REPLACE FUNCTION pls_verifica_dt_prorrogado ( nr_titulo_p bigint, dt_pagamento_p timestamp) RETURNS varchar AS $body$
DECLARE


dt_pagamento_previsto_w	titulo_receber.dt_pagamento_previsto%type;
ie_prorrogado_w		varchar(1);


BEGIN

select	dt_pagamento_previsto
into STRICT	dt_pagamento_previsto_w
from	titulo_receber
where	nr_titulo = nr_titulo_p;

if (trunc(dt_pagamento_p,'dd') > trunc(dt_pagamento_previsto_w,'dd')) then
	ie_prorrogado_w := 'S';
end if;

return	ie_prorrogado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
