CREATE OR REPLACE FUNCTION pls_verifica_proc_mat_audit ( nr_seq_auditoria_p bigint, cd_procedimento_p text, ie_origem_proced_p text, nr_seq_material_p bigint, nr_seq_item_auditoria_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se o procedimento/material já existe na guia/requisição auditada
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_retorno_w			varchar(1)	:= 'N';
qt_proc_aud_w			bigint;
qt_mat_aud_w			bigint;


BEGIN

if (nr_seq_auditoria_p IS NOT NULL AND nr_seq_auditoria_p::text <> '') then
	if	(cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '' AND ie_origem_proced_p IS NOT NULL AND ie_origem_proced_p::text <> '') then
		select	count(1)
		into STRICT	qt_proc_aud_w
		from	pls_auditoria_item
		where	nr_seq_auditoria	= nr_seq_auditoria_p
		and	cd_procedimento		= cd_procedimento_p
		and	ie_origem_proced	= ie_origem_proced_p
		and	((nr_sequencia		<> nr_seq_item_auditoria_p) or (coalesce(nr_seq_item_auditoria_p::text, '') = ''))
		and	ie_status 		in ('A', 'P', 'J');

	elsif (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
		select	count(1)
		into STRICT	qt_mat_aud_w
		from	pls_auditoria_item
		where	nr_seq_auditoria	= nr_seq_auditoria_p
		and	nr_seq_material		= nr_seq_material_p
		and	((nr_sequencia		<> nr_seq_item_auditoria_p) or (coalesce(nr_seq_item_auditoria_p::text, '') = ''))
		and	ie_status 		in ('A', 'P', 'J');
	end if;
end if;



if	((qt_proc_aud_w	> 0) or (qt_mat_aud_w > 0)) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
