CREATE OR REPLACE FUNCTION pls_verifica_regra_atrib ( nm_tabela_p text, nm_atributo_p text, nr_tamanho_campo_p bigint, ie_tipo_verificacao_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter o campo informado no parâmetro possui regra de atributo e se esta regra
se encaixa com as informações informadas;

ie_tipo_verificacao_p:
	1 - Qt. Min/Max

-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(1);
qt_tam_min_w			smallint;
qt_tam_max_w			smallint;
ie_entrou_regra_w		varchar(1);

BEGIN

if (ie_tipo_verificacao_p = 1) then
	begin
		select	qt_tam_min,
			qt_tam_max
		into STRICT	qt_tam_min_w,
			qt_tam_max_w
		from	tabela_atrib_regra
		where	nm_tabela	= nm_tabela_p
		and	nm_atributo	= nm_atributo_p
		and	ie_enable	= 'S';
	exception
	when others then
		qt_tam_min_w	:= null;
		qt_tam_max_w	:= null;
	end;

	if (qt_tam_min_w IS NOT NULL AND qt_tam_min_w::text <> '') and (coalesce(qt_tam_max_w::text, '') = '') and (nr_tamanho_campo_p < qt_tam_min_w) then
		ie_entrou_regra_w	:= 'S';

	elsif (coalesce(qt_tam_min_w::text, '') = '') and (qt_tam_max_w IS NOT NULL AND qt_tam_max_w::text <> '') and (nr_tamanho_campo_p > qt_tam_max_w) then
		ie_entrou_regra_w	:= 'S';

	elsif (qt_tam_min_w IS NOT NULL AND qt_tam_min_w::text <> '') and (qt_tam_max_w IS NOT NULL AND qt_tam_max_w::text <> '') and ((nr_tamanho_campo_p < qt_tam_min_w) or (nr_tamanho_campo_p > qt_tam_max_w)) then
		ie_entrou_regra_w	:= 'S';

	else
		ie_entrou_regra_w	:= 'N';
	end if;
end if;

ds_retorno_w	:= ie_entrou_regra_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
