SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_verifica_se_exige_proc_lib ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, nr_seq_execucao_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_proc_lib_w		varchar(2)	:= 'N';
qt_pacotes_guia_w	bigint;
ie_retorno_w		varchar(2)	:= 'N';


BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	count(1)
	into STRICT	qt_pacotes_guia_w
	from	pls_guia_plano_proc
	where	nr_seq_guia	= nr_seq_guia_p
	and	(nr_seq_pacote IS NOT NULL AND nr_seq_pacote::text <> '');

	select	max('S')
	into STRICT	ie_proc_lib_w
	from	pls_guia_plano_proc
	where	nr_seq_guia	= nr_seq_guia_p
	and	coalesce(nr_seq_pacote::text, '') = ''
	and	ie_status in ('S', 'I');

elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	select	count(1)
	into STRICT	qt_pacotes_guia_w
	from	pls_requisicao_proc
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and	(nr_seq_pacote IS NOT NULL AND nr_seq_pacote::text <> '');

	select	max('S')
	into STRICT	ie_proc_lib_w
	from	pls_requisicao_proc
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and	coalesce(nr_seq_pacote::text, '') = ''
	and	ie_status in ('S', 'I');

elsif (nr_seq_execucao_p IS NOT NULL AND nr_seq_execucao_p::text <> '') then
	select	count(1)
	into STRICT	qt_pacotes_guia_w
	from	pls_execucao_req_item
	where	nr_seq_execucao		= nr_seq_execucao_p
	and	(nr_seq_pacote IS NOT NULL AND nr_seq_pacote::text <> '');

	select	max('S')
	into STRICT	ie_proc_lib_w
	from	pls_execucao_req_item
	where	nr_seq_execucao		= nr_seq_execucao_p
	and	coalesce(nr_seq_pacote::text, '') = ''
	and	ie_situacao in ('S', 'I');

end if;

if (ie_proc_lib_w	= 'S')		and (qt_pacotes_guia_w	> 0) then
	ie_retorno_w	:= 'N';
elsif (coalesce(ie_proc_lib_w::text, '') = '')	and (qt_pacotes_guia_w	> 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
