CREATE OR REPLACE FUNCTION pls_verif_acat_neg_conta_rec ( nr_seq_conta_rec_p pls_rec_glosa_conta.nr_sequencia%type, nr_seq_protocolo_p pls_rec_glosa_protocolo.nr_sequencia%type, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(5) := '';
qnt_acatado_w		integer;
qnt_negado_w		integer;
qnt_total_acatado_w	integer;
qnt_total_negado_w	integer;
qt_analise_rec_w	integer;

C01 CURSOR(nr_seq_protocolo_pc		pls_rec_glosa_protocolo.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia nr_seq_conta_rec
	from	pls_rec_glosa_conta a
	where	a.nr_seq_protocolo	= nr_seq_protocolo_pc
	and	a.ie_status		!= '3';

BEGIN
qnt_total_acatado_w := 0;
qnt_total_negado_w := 0;

if (nr_seq_conta_rec_p IS NOT NULL AND nr_seq_conta_rec_p::text <> '') then
	select	sum(cont)
	into STRICT	qnt_acatado_w
	from (SELECT	count(1) cont
		from	pls_rec_glosa_proc
		where	nr_seq_conta_rec = nr_seq_conta_rec_p
		and	ie_status = 3
		
union all

		SELECT	count(1) cont
		from	pls_rec_glosa_mat
		where	nr_seq_conta_rec = nr_seq_conta_rec_p
		and	ie_status = 3) alias4;

	select	sum(cont)
	into STRICT	qnt_negado_w
	from (SELECT	count(1) cont
		from	pls_rec_glosa_proc
		where	nr_seq_conta_rec = nr_seq_conta_rec_p
		and	ie_status = 4
		
union all

		SELECT	count(1) cont
		from	pls_rec_glosa_mat
		where	nr_seq_conta_rec = nr_seq_conta_rec_p
		and	ie_status = 4) alias3;

qnt_total_acatado_w := qnt_acatado_w;
qnt_total_negado_w := qnt_negado_w;

elsif (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then

	for r_C01_w in C01(nr_seq_protocolo_p) loop
		qnt_acatado_w := 0;
		qnt_negado_w := 0;

		select	sum(cont)
		into STRICT	qnt_acatado_w
		from (SELECT	count(1) cont
			from	pls_rec_glosa_proc
			where	nr_seq_conta_rec = r_C01_w.nr_seq_conta_rec
			and	ie_status = 3
			
union all

			SELECT	count(1) cont
			from	pls_rec_glosa_mat
			where	nr_seq_conta_rec = r_C01_w.nr_seq_conta_rec
			and	ie_status = 3) alias3;

		select	sum(cont)
		into STRICT	qnt_negado_w
		from (SELECT	count(1) cont
			from	pls_rec_glosa_proc
			where	nr_seq_conta_rec = r_C01_w.nr_seq_conta_rec
			and	ie_status = 4
			
union all

			SELECT	count(1) cont
			from	pls_rec_glosa_mat
			where	nr_seq_conta_rec = r_C01_w.nr_seq_conta_rec
			and	ie_status = 4) alias3;

		qnt_total_acatado_w := qnt_total_acatado_w + qnt_acatado_w;
		qnt_total_negado_w := qnt_total_negado_w + qnt_negado_w;
	end loop;
end if;

if ( qnt_total_acatado_w = 0 ) and ( qnt_total_negado_w > 0 ) then
	ds_retorno_w := 'N';
elsif ( qnt_total_negado_w = 0 ) and ( qnt_total_acatado_w > 0 ) then
	ds_retorno_w := 'A';
end if;

return	ds_retorno_w;

end;
$body$
