CREATE OR REPLACE FUNCTION pls_verif_documento_prog_reaj ( nr_seq_prog_reaj_p pls_prog_reaj_coletivo.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


qt_extrato_w bigint;


BEGIN

select count(1)
into STRICT qt_extrato_w
from (	SELECT nr_sequencia
	from   pls_prog_reaj_documentacao
	where  nr_seq_prog_reaj = nr_seq_prog_reaj_p
	
union all

	SELECT 	a.nr_sequencia
	from 	pls_prog_reaj_documentacao a
	where exists ( select 1
			from 	pls_prog_reaj_colet_lote y,
				pls_prog_reaj_coletivo x
			where 	y.nr_sequencia = a.nr_seq_lote
			and 	y.nr_sequencia = x.nr_seq_lote
			and 	x.nr_sequencia = nr_seq_prog_reaj_p)
	and coalesce(a.nr_seq_prog_reaj::text, '') = '') alias3;

return qt_extrato_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
