SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_verif_envia_resp_aud_web (nr_seq_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE



ie_envia_w		varchar(1) := 'N';
qt_registros_w		bigint;


BEGIN

select	count(1)
into STRICT	qt_registros_w
from 	pls_requisicao
where	nr_Sequencia = nr_seq_requisicao_p
and	coalesce(ie_tipo_processo,'X') = 'I'
and	coalesce(ie_tipo_intercambio,'X') = 'E';

if (qt_registros_w > 0) then
	select	count(1)
	into STRICT	qt_registros_w
	from	pls_auditoria
	where 	nr_seq_requisicao = nr_seq_requisicao_p
	and	ie_status = 'F';

	if (qt_registros_w = 1) then
		ie_envia_w := 'S';
	end if;
end if;

return	ie_envia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
