CREATE OR REPLACE FUNCTION pls_verif_obtem_produto_tarja ( cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255);

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
Verificar se o produto do beneficiário eventual deverá ser obtido pela tarja magnética, ao cadastrá-lo pela primeira vez. 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ x] Tasy (Delphi/Java) [  x] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 

BEGIN 
begin 
	select 	coalesce(ie_produto_tarja, 'S') 
	into STRICT	ds_retorno_w 
	from	pls_parametros 
	where	cd_estabelecimento	= cd_estabelecimento_p;
exception 
when others then 
	select 	coalesce(max(ie_produto_tarja), 'S') 
	into STRICT	ds_retorno_w 
	from	pls_parametros;
end;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
