SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_verif_se_perm_alt_dt_resc ( nr_seq_solicitacao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w			varchar(1);
qt_benef_solicitacao_w		bigint;
qt_benef_permite_alt_venc_w	bigint;
	

BEGIN

select	count(1)
into STRICT	qt_benef_solicitacao_w
from	pls_solic_rescisao_benef
where	nr_seq_solicitacao = nr_seq_solicitacao_p;

select	count(1)
into STRICT	qt_benef_permite_alt_venc_w
from	pls_solic_rescisao_benef a,
	pls_motivo_cancelamento b
where	b.nr_sequencia = a.nr_seq_motivo_rescisao
and	a.nr_seq_solicitacao = nr_seq_solicitacao_p
and	coalesce(b.ie_permite_alt_data_solic, 'N') = 'S';

if (qt_benef_solicitacao_w = qt_benef_permite_alt_venc_w) then
	ie_permite_w	:= 'S';
else
	ie_permite_w	:= 'N';
end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
