CREATE OR REPLACE FUNCTION pls_vl_intern_faixa_etaria ( qt_idade_inicial_p bigint, qt_idade_final_p bigint, ie_tipo_data_p text, dt_inicial_p timestamp, dt_final_p timestamp, ie_tipo_conta_p text) RETURNS bigint AS $body$
DECLARE


vl_media_total_w		double precision;


BEGIN
vl_media_total_w	:= 0;

select	avg(vl_total)
into STRICT	vl_media_total_w
from	(
	SELECT	sum(c.vl_total) vl_total,
		c.cd_guia_referencia
	from	pessoa_fisica		d,
		pls_conta		c,
		pls_segurado		b,
		pls_protocolo_conta	a
	where	d.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and	b.nr_sequencia		= c.nr_seq_segurado
	and	a.nr_sequencia		= c.nr_seq_protocolo
	and	((ie_tipo_data_p = 'P' and a.dt_protocolo between dt_inicial_p and fim_dia(dt_final_p)) or (ie_tipo_data_p = 'M' and a.dt_mes_competencia between dt_inicial_p and fim_dia(dt_final_p)) or (ie_tipo_data_p = 'N'))
	and	coalesce(c.ie_tipo_conta, 'O') = ie_tipo_conta_p
	and	obter_idade(d.dt_nascimento, c.dt_atendimento_referencia, 'A') >=  qt_idade_inicial_p
	and	obter_idade(d.dt_nascimento, c.dt_atendimento_referencia, 'A') <=  qt_idade_final_p
	and	pls_obter_se_internado(c.nr_sequencia,c.ie_tipo_conta) = 'S'
	group by c.cd_guia_referencia
) alias12;

return	vl_media_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
