CREATE OR REPLACE FUNCTION plt_juntar_prescritores ( nm_prescritores_p text, nm_prescritor_p text) RETURNS varchar AS $body$
DECLARE


nm_prescritores_w	varchar(2000);


BEGIN
nm_prescritores_w := nm_prescritores_p;

if (coalesce(nm_prescritores_w::text, '') = '') then
	nm_prescritores_w := nm_prescritor_p;
elsif (obter_se_contido_char(' '||nm_prescritor_p, '( ' || nm_prescritores_w || ')') = 'N') then
	nm_prescritores_w := nm_prescritores_w || ', ' || nm_prescritor_p;
end if;

return nm_prescritores_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
