SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION plt_obter_desc_alternativa ( ie_tipo_item_p text, nr_seq_regra_p text) RETURNS varchar AS $body$
DECLARE



ds_nome_w	varchar(255);

BEGIN


Select	substr(max(ds_nome_alternativo),1,255)
into STRICT	ds_nome_w
from	plt_regra_ordem_item
where	nr_seq_grupo = nr_seq_regra_p
and	ie_tipo_item = ie_tipo_item_p;

if (coalesce(ds_nome_w::text, '') = '') then
	select	substr(max(obter_desc_expressao(cd_exp_valor_dominio, ds_valor_dominio)),1,255)
	into STRICT	ds_nome_w
	from    valor_dominio
	where   cd_dominio  = 3270
	and	vl_dominio  = ie_tipo_item_p;
end if;

return	ds_nome_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
