CREATE OR REPLACE FUNCTION plt_obter_dt_extensao (dt_extensao_p timestamp, dt_validade_prescr_p timestamp, nr_horas_validade_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_extensao_w		timestamp;


BEGIN

if (dt_extensao_p IS NOT NULL AND dt_extensao_p::text <> '') then
	dt_extensao_w	:= dt_extensao_p;
elsif (coalesce(nr_horas_validade_p,0)	> 24) then
	dt_extensao_w	:= dt_validade_prescr_p;
end if;

return	dt_extensao_w;

end;
$body$
